import { NgModule } from '@angular/core';
import { RouterModule, Routes } from '@angular/router';
import { PersonFormComponent } from '../components/person-form/person-form.component';
import { HomeComponent } from '../components/home/home.component';
import { PersonTableComponent } from '../components/person-table/person-table.component';

const routes: Routes = [
  { path: '', component: HomeComponent },  // Página inicial
  { path: 'people', component: PersonTableComponent }, // A rota correta para a tabela de pessoas
  { path: 'person-form', component: PersonFormComponent },  // Formulário para adicionar ou editar pessoa
  { path: '**', redirectTo: '', pathMatch: 'full' }  // Redirecionamento para a homepage em caso de rota não encontrada
];

@NgModule({
  imports: [RouterModule.forRoot(routes)],  // Use `forRoot` para configuração principal de rotas
  exports: [RouterModule]
})
export class PersonRoutingModule {}
