import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { PersonService } from '../../services/person.service';
import { Person } from '../../models/person';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-person-form',
  templateUrl: './person-form.component.html',
  styleUrls: ['./person-form.component.scss']
})
export class PersonFormComponent implements OnInit {
  personForm: FormGroup;
  isEditing = false;
  personId?: number;

  constructor(
    private fb: FormBuilder,
    private personService: PersonService,
    private route: ActivatedRoute,
    private router: Router,
    private http: HttpClient
  ) {
    this.personForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      phone: ['', [Validators.required, Validators.pattern('^[0-9]{10,11}$')]],
      zipCode: ['', [Validators.required, Validators.minLength(8), Validators.maxLength(8)]],
      address: ['', Validators.required],
      city: ['', Validators.required],
      state: ['', Validators.required],
      active: [true]
    });
  }

  ngOnInit(): void {
    this.personId = Number(this.route.snapshot.paramMap.get('id'));
    if (this.personId) {
      this.isEditing = true;
      this.personService.getPersonById(this.personId).subscribe(person => {
        this.personForm.patchValue(person);
      });
    }
  }

  onSubmit(): void {
    if (this.personForm.invalid) {
      this.personForm.markAllAsTouched(); // Marca todos os campos como tocados para exibir erros
      return;
    }
  
    const person: Person = this.personForm.value;
  
    if (this.isEditing) {
      this.personService.updatePerson(this.personId!, person).subscribe(() => {
        this.router.navigate(['/people']);
      });
    } else {
      this.personService.createPerson(person).subscribe(() => {
        this.router.navigate(['/people']);
      });
    }
  }
  

  fetchAddress(): void {
    const zipCode = this.personForm.get('zipCode')?.value;
    if (zipCode.length === 8) {
      this.http.get<any>(`https://viazipCode.com.br/ws/${zipCode}/json/`).subscribe(data => {
        if (!data.erro) {
          this.personForm.patchValue({
            address: `${data.logradouro}, ${data.bairro}`,
            city: data.localidade,
            state: data.uf
          });
        }
      });
    }
  }

  onCancel(): void {
    this.router.navigate(['/people']);
  }
}
<div class="container">
  <h2>{{ isEditing ? 'Edit Person' : 'Add Person' }}</h2>

  <form [formGroup]="personForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label for="name">Name</label>
      <input id="name" formControlName="name" type="text" required />
    </div>

    <div class="form-group">
      <label for="email">Email</label>
      <input id="email" formControlName="email" type="email" required />
      <div class="error-message" *ngIf="personForm.get('email')?.invalid && personForm.get('email')?.touched">
        Email inválido
      </div>
    </div>

    <div class="form-group">
      <label for="phone">Contact Number</label>
      <input id="phone" formControlName="phone" type="text" required />
      <div class="error-message" *ngIf="personForm.get('phone')?.invalid && personForm.get('phone')?.touched">
        O telefone deve ter 10 ou 11 dígitos numéricos.
      </div>
    </div>


    <div class="form-group">
      <label for="zipCode">zipCode</label>
      <input id="zipCode" formControlName="zipCode" type="text" (blur)="fetchAddress()" required />
    </div>

    <div class="form-group">
      <label for="address">Address</label>
      <input id="address" formControlName="address" type="text" required />
    </div>

    <div class="form-group">
      <label for="city">City</label>
      <input id="city" formControlName="city" type="text" required />
    </div>

    <div class="form-group">
      <label for="state">State</label>
      <input id="state" formControlName="state" type="text" required />
    </div>

    <div class="button-group">
      <button type="submit" class="save-btn" [disabled]="personForm.invalid">
        {{ isEditing ? 'Update' : 'Save' }}
      </button>

      <button type="button" class="cancel-btn" (click)="onCancel()">Cancel</button>
    </div>
  </form>
</div>
