import { Component, OnInit } from '@angular/core';
import { PersonService } from '../../services/person.service';
import { Person } from '../../models/person';

@Component({
  selector: 'app-person-list',
  templateUrl: './person-list.component.html',
  styleUrls: ['./person-list.component.scss']
})
export class PersonListComponent implements OnInit {
  people: Person[] = [];

  constructor(private personService: PersonService) {}

  ngOnInit(): void {
    this.loadPeople();
  }

  loadPeople(): void {
    this.personService.getPeople().subscribe(data => {
      this.people = data;
    });
  }

  deletePerson(id: number): void {
    if (confirm('Are you sure you want to delete this person?')) {
      this.personService.deletePerson(id).subscribe(() => {
        this.loadPeople();
      });
    }
  }

  getContactType(contactType: number): string {
    switch (contactType) {
      case 1:
        return 'Telefone';
      case 2:
        return 'Email';
      case 3:
        return 'Endereço';
      default:
        return 'Desconhecido';
    }
  }
}
<h2>Lista de Pessoas</h2>

<table>
  <thead>
    <tr>
      <th>ID</th>
      <th>Nome</th>
      <th>Endereço</th>
      <th>zipCode</th>
      <th>UF</th>
      <th>Ativo</th>
      <th>Contatos</th>
      <th>Tipo de Contato</th> 
      <th>Ações</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let person of people">
      <td>{{ person.id }}</td>
      <td>{{ person.name }}</td>
      <td>{{ person.address }}</td>
      <td>{{ person.zipCode }}</td>
      <td>{{ person.state }}</td>
      <td>{{ person.active ? 'Sim' : 'Não' }}</td>
      <td>{{ person.contacts.join(', ') }}</td>
      <td>{{ getContactType(person.contactType) }}</td> 
      <td>
        <button (click)="deletePerson(person.id)">Excluir</button>
      </td>
    </tr>
  </tbody>
</table>

<button routerLink="/person/new">Adicionar Pessoa</button>
