import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';
import { PersonService } from '../../services/person.service';
import { Person } from '../../models/person';

@Component({
  selector: 'app-person-table',
  templateUrl: './person-table.component.html',
  styleUrls: ['./person-table.component.scss']
})
export class PersonTableComponent {
  @Input() people: Person[] = [];

  constructor(private personService: PersonService, private router: Router) {}

  editPerson(id: number): void {
    this.router.navigate(['/person-form', id]);
  }

  deletePerson(id: number): void {
    if (confirm('Tem certeza que deseja excluir esta pessoa?')) {
      this.personService.deletePerson(id).subscribe(() => {
        this.people = this.people.filter(person => person.id !== id);
      });
    }
  }

  addPerson(): void {
    this.router.navigate(['/person-form']); // Redireciona para a página de cadastro
  }
}
<div class="container">
  <h2>Lista de Pessoas</h2>
  <button class="add-button" (click)="addPerson()">Adicionar Pessoa</button>
  <table class="styled-table">
    <thead>
      <tr>
        <th>ID</th>
        <th>Nome</th>
        <th>Endereço</th>
        <th>zipCode</th>
        <th>UF</th>
        <th>Ativo</th>
        <th>Contatos</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let person of people">
        <td>{{ person.id }}</td>
        <td>{{ person.name }}</td>
        <td>{{ person.address }}</td>
        <td>{{ person.zipCode }}</td>
        <td>{{ person.state }}</td>
        <td>{{ person.active ? 'Sim' : 'Não' }}</td>
        <td>{{ person.contacts.join(', ') }}</td>
        <td>
          <button class="edit-button" (click)="editPerson(person.id)">Editar</button>
          <button class="delete-button" (click)="deletePerson(person.id)">Excluir</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
